mapper.select:

 		select user_name
		from user_table
		where user_id = #{user_id}


 			select user_idx, user_name 
			from user_table 
			where user_id=#{user_id} and user_pw=#{user_pw}


 			select user_id, user_name 
			from user_table
			where user_idx = #{user_idx}

mapper.update:

 			update user_table
			set user_pw = #{user_pw} 
			where user_idx = #{user_idx}
